package week1.day1;

public class ConvertNegativeNumbertoPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Negative_Input = -30;
		int Negative_Conv = -1;
		if (Negative_Input < 0) {
			int convert = Negative_Input * Negative_Conv;
			System.out.println("The negative number is converted to positive: "+convert);
		}
	}
}
